mapper.select:
select
  ph.home_id, ph.home_name, ph.player_uuid, ph.x, ph.y, ph.z, ph.world, ph.show_material
from ari_player_home as ph
where ph.player_uuid = #{playerUUID}

    limit #{page.limit} offset #{page.offset}

select ph.home_id, ph.home_name, ph.player_uuid, ph.x, ph.y, ph.z, ph.world, ph.show_material
from ari_player_home as ph
where ph.home_id = #{home_id} and player_uuid = #{playerUUID}

mapper.update:
update ari_player_home
set home_name = #{playerhome.homeName}, x = #{playerhome.x}, y = #{playerhome.y}, z = #{playerhome.z}, world = #{playerhome.world}, show_material = #{playerhome.showMaterial}
where home_id = #{playerhome.homeId} and player_uuid = #{playerhome.playerUUID}
